import React, { useEffect, useState } from 'react';
import { ActivityIndicator, FlatList, Text, View, Alert, StyleSheet } from 'react-native';


const Carros = () => {
  const lista = [
    {
      marca: "Fiat",
    },
    {
      marca: "GM",      
    },
    {
      marca: "VW",
    },
    {
      marca: "Ford",
    }
  ]
  const [marcaSelecionada,onChangeMarcaSelecionada] = React.useState('');
  const [isLoading, setLoading] = useState(true);

  const ItemView = ({ item }) => {
    return (
      <Text style={styles.itemStyle}
        onPress={() =>{onChangeMarcaSelecionada(item.marca), getMarca()}}
      >
        {item.marca}
      </Text>
    );
  }

  const getMarca = async () => {
    try {      
      const response = await fetch('http://152.92.181.90:8080/CursoJavaScript/EnviarMenuJson',{	  
        method: 'POST',
          headers: {
          //	Accept: 'application/json','Content-Type': 'application/json;charset=UTF-8',			
           'Content-type': 'application/x-www-form-urlencoded; charset=UTF-8',
        },
          body: JSON.stringify({marca: marcaSelecionada})
        }); 
        console.log(await response.json());
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <View style={styles.container}>
      {isLoading ? (
        <ActivityIndicator />
      ) : (
        <FlatList
          data={lista}
          keyExtractor={({ marca }) => marca}
          renderItem={ItemView}
          onEndReachedThreshold={0.1}
        />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
  flex:1,
  margin: 10,
  paddingTop: 40,
  backgroundColor: 'white',
  },
  itemStyle: {
  backgroundColor: '#0066CC',
  padding: 10,
  marginVertical: 8,
  color: 'white',
  },
  headerFooterStyle: {
  width: '100%',
  height: 45,
  backgroundColor: '#606070',
  },
  textStyle: {
  textAlign: 'center',
  color: '#fff',
  fontSize: 18,
  padding: 7,
  },
  });
export default Carros;
