import React, { useEffect, useState } from 'react';
import { ActivityIndicator, FlatList, Text, View, Alert, StyleSheet } from 'react-native';


const Carros = () => {
  const lista = [
    {
      marca: "Fiat",
    },
    {
      marca: "GM",      
    },
    {
      marca: "VW",
    },
    {
      marca: "Ford",
    }
  ]
  const [marcaSelecionada,onChangeMarcaSelecionada] = React.useState('');
  const [isLoading, setLoading] = useState(true);
  const [modalVisible, setModalVisible] = useState(false);

  const ItemView = ({ item }) => {
    return (
      <Text style={styles.itemStyle}
        onPress={() =>{setModalVisible(true),onChangeMarcaSelecionada(item.marca), getMarca()}}
      >
        {item.marca}
      </Text>
    );
  }

  const getMarca = async () => {
     try {      
      const response = await fetch('http://152.92.181.90:8080/CursoJavaScript/EnviarMenuJson',{	  
        method: 'POST',
          headers: {
          //	Accept: 'application/json','Content-Type': 'application/json;charset=UTF-8',			
           'Content-type': 'application/x-www-form-urlencoded; charset=UTF-8',
        },
          body: JSON.stringify({marca: marcaSelecionada})
          });  
        console.log(await response.json());
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getMarca();
  }, []);

  const MeuModal = () => {
    return (
      <Modal
        animationType="fade"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          //  Alert.alert('Modal foi fechado!');
          setModalVisible(!modalVisible);
        }}>
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <Text style={styles.modalText}>
              {marca}{/* o que aparece no modal */}
              </Text>
            <Button
              onPress={() => setModalVisible(false)}
              title="Fechar"
              color="red"
            />
          </View>
        </View>
      </Modal>
    )
  }

  return (
    <View style={styles.container}>
      {isLoading ? (
        <ActivityIndicator />
      ) : (
        <FlatList
          data={lista}
          keyExtractor={({ marca }) => marca}
          renderItem={ItemView}
        />
        )}
        {modalVisible ? <MeuModal /> : ''}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    margin: 10,
    paddingTop: 40,
    backgroundColor: 'white',
  },
  itemStyle: {
    backgroundColor: '#ffc700',
    padding: 10,
    marginVertical: 8,
    color: 'black',
  },
  centeredView: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 22,
  },
  modalView: {
    margin: 20,
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 35,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  modalText: {
    marginBottom: 15,
    textAlign: 'center',
  },
  });
export default Carros;
