import React, { useEffect, useState } from 'react';
import { ActivityIndicator, FlatList, Text, View, Alert, StyleSheet } from 'react-native';


const Carros = () => {
  const lista = [
    {
      marca: "Fiat",
    },
    {
      marca: "GM",      
    },
    {
      marca: "VW",
    },
    {
      marca: "Ford",
    }
  ]
  const [marcaSelecionada,onChangeMarcaSelecionada] = React.useState('');
  const [isLoading, setLoading] = useState(true);
  const [data, setData] = useState(null);
  const [title, setTitle] = useState('');
  const [rodape, setRodape] = useState('');

  const getMovies = async () => {
    try {

      const response = await fetch('http://152.92.181.90:8080/CursoJavaScript/EnviarMenuJson'); 
        console.log(await response.json());
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const Titulo = () => {
    return (
      <View style={styles.headerFooterStyle}>
        <Text style={styles.textStyle}>
          {title}
        </Text>
      </View>
    );
  };

  const Rodape = () => {
    return (
      <View style={styles.headerFooterStyle}>
        <Text style={styles.textStyle}>
          {rodape}
        </Text>
      </View>
    );
  };

  useEffect(() => {
    getMovies();
  }, []);

  return (
    <View style={styles.container}>
      {isLoading ? (
        <ActivityIndicator />
      ) : (
        <FlatList
          data={lista}
          keyExtractor={({ marca }) => marca}
          renderItem={({ item }) => (
            <Text style={styles.itemStyle}>
              {item.marca}
            </Text>
          )}
        />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 35,
  },
  input: {
    width: '100%',
    height: 40,
    paddingHorizontal: 5,
    borderWidth: 0.5,
    marginTop: 15,	
  },
  titulo: {    
    margin: 6,    
    padding: 5,
	fontWeight: 'bold',
  },  
});
export default Carros;
